export interface ApplicationOptions {
  /**
   * The Nest application author.
   */
  author?: string;
  /**
   * The Nest application description.
   */
  description?: string;
  /**
   * The Nest application name.
   */
  name: string;
  /**
   * The Nest application version.
   */
  version?: string;
  /**
   * The Nest configuration language.
   */
  language?: string;
}
